import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Map from './Map';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './Home.css';

const Home = () => {
  const [properties, setProperties] = useState([]);
  const [search, setSearch] = useState('');
  const [featuredProperties, setFeaturedProperties] = useState([]);
  const [region, setRegion] = useState('');
  const [county, setCounty] = useState('');
  const [subcounty, setSubcounty] = useState('');

  const fetchProperties = async () => {
    try {
      let url = 'http://localhost:5000/properties';

      // Adding filters based on region, county, subcounty
      if (region) {
        url += `?region=${region}`;
        if (county) {
          url += `&county=${county}`;
          if (subcounty) {
            url += `&subcounty=${subcounty}`;
          }
        }
      }

      const response = await axios.get(url);
      setProperties(response.data);
    } catch (error) {
      console.error('Error fetching properties:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  const fetchFeaturedProperties = async () => {
    try {
      const response = await axios.get('http://localhost:5000/properties/featured');
      setFeaturedProperties(response.data);
    } catch (error) {
      console.error('Error fetching featured properties:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  useEffect(() => {
    fetchProperties();
    fetchFeaturedProperties();
  }, [region, county, subcounty]);

  const handleSearchClick = () => {
    // Fetch properties with the current search criteria
    fetchProperties();
  };

  const handleRegionChange = (e) => {
    setRegion(e.target.value);
  };

  const handleCountyChange = (e) => {
    setCounty(e.target.value);
  };

  const handleSubcountyChange = (e) => {
    setSubcounty(e.target.value);
  };

  const filteredProperties = properties.filter((property) =>
    property.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <div className="search-container">
        <div className="filters">
          <label htmlFor="region">Region:</label>
          <select id="region" value={region} onChange={handleRegionChange}>
            <option value="">Select Region</option>
            {/* Add dynamic options if needed */}
          </select>

          <label htmlFor="county">County:</label>
          <select id="county" value={county} onChange={handleCountyChange}>
            <option value="">Select County</option>
            {/* Add dynamic options if needed */}
          </select>

          <label htmlFor="subcounty">Subcounty:</label>
          <select id="subcounty" value={subcounty} onChange={handleSubcountyChange}>
            <option value="">Select Subcounty</option>
            {/* Add dynamic options if needed */}
          </select>
        </div>

        <div className="search-bar">
          <input
            type="text"
            placeholder="Search properties..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button onClick={handleSearchClick} className="search-button">
            <FontAwesomeIcon icon={faSearch} />
          </button>
        </div>
      </div>

      <Map properties={filteredProperties} />

      <h2>Featured Rentals</h2>
      <div className="slider">
        {featuredProperties.map((property) => (
          <div key={property._id} className="property-card">
            <img src={property.imageUrl} alt={property.title} className="property-image" />
            <div className="property-info">
              <h3>{property.title}</h3>
              <p>{property.description}</p>
              <p>Price: ${property.price}</p>
            </div>
          </div>
        ))}
      </div>

      <section className="hero-section">
        <h1>We help you find your desired rental with ease</h1>
        <p>Discover your next home with Hama Bwana. Our platform offers a wide range of rentals across Kenya to suit your needs.</p>
        <button onClick={() => window.location.href = '/register'}>Get Started</button>
      </section>

      <section className="services-section">
        <h2>Our Services</h2>
        <ul>
          <li>Extensive property listings</li>
          <li>Easy search and filter options</li>
          <li>Secure and reliable platform</li>
          <li>24/7 customer support</li>
        </ul>
      </section>

      <section className="about-section">
        <h2>About Us</h2>
        <p>Hama Bwana is a leading real estate platform dedicated to helping you find your perfect rental. We offer a vast selection of properties and top-notch services to make your search as smooth as possible.</p>
      </section>

      <section className="blog-section">
        <h2>Latest News</h2>
        <div className="blog-posts">
          {/* Add dynamic blog posts if needed */}
        </div>
      </section>

      <section className="testimonials-section">
        <h2>Testimonials</h2>
        <div className="testimonials">
          {/* Add dynamic testimonials if needed */}
        </div>
      </section>

      <section className="contact-section">
        <h2>Contact Us</h2>
        <p>For any inquiries, please reach out to us at contact@hamabwana.com or call us at +254-700-000-000.</p>
      </section>
    </div>
  );
};

export default Home;
