import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecentRentals = () => {
  const [properties, setProperties] = useState([]);

  // Define demo houses
  const demoHouses = [
    {
      _id: 1,
      title: 'Luxury Villa in Nairobi',
      description: 'A beautiful villa with 4 bedrooms and a swimming pool.',
      price: 150000,
    },
    {
      _id: 2,
      title: 'Cozy Apartment in Mombasa',
      description: 'A 2-bedroom apartment located near the beach.',
      price: 75000,
    },
    {
      _id: 3,
      title: 'Spacious Bungalow in Kisumu',
      description: 'A modern 3-bedroom bungalow with a large garden.',
      price: 120000,
    },
    {
      _id: 4,
      title: 'Penthouse in Nakuru',
      description: 'A luxurious penthouse with stunning lake views.',
      price: 180000,
    },
  ];

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await axios.get('http://localhost:5000/properties/recent');
        setProperties(response.data);
      } catch (error) {
        console.error('Error fetching recent rentals:', error);
        // Handle error (e.g., show error message to user)
      }
    };

    fetchProperties();
  }, []);

  return (
    <div className="container">
      <h2>Recent Rentals</h2>
      <div>
        {(properties.length ? properties : demoHouses).map((property) => (
          <div key={property._id} className="property-card">
            <h3>{property.title}</h3>
            <p>{property.description}</p>
            <p>Price: Ksh {property.price}</p>
            {/* You can add images and videos */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecentRentals;
