// src/components/Register.js
import React, { useState } from 'react';
import axios from 'axios';
import './Register.css';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    description: '',
    password: '',
    profilePicture: null,
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      profilePicture: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataWithImage = new FormData();
    formDataWithImage.append('name', formData.name);
    formDataWithImage.append('email', formData.email);
    formDataWithImage.append('description', formData.description);
    formDataWithImage.append('password', formData.password);
    if (formData.profilePicture) {
      formDataWithImage.append('profilePicture', formData.profilePicture);
    }

    try {
      await axios.post('http://localhost:5000/register', formDataWithImage);
      navigate('/login');
    } catch (error) {
      console.error('Error during registration:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  return (
    <div className="register-container">
      <h2 className="register-title">Register</h2>
      <form onSubmit={handleSubmit} className="register-form">
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          className="register-input"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          className="register-input"
          required
        />
        <textarea
          name="description"
          placeholder="Short description"
          value={formData.description}
          onChange={handleChange}
          className="register-textarea"
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          className="register-input"
          required
        />
        <input
          type="file"
          name="profilePicture"
          onChange={handleFileChange}
          className="register-file-input"
          required
        />
        <button type="submit" className="register-button">Register</button>
      </form>
      <p className="register-login">
        Or already have an account? <button className="register-login-button" onClick={() => navigate('/login')}>Login here</button>
      </p>
    </div>
  );
};

export default Register;
