import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RecentRentals.css'; // Assuming you will add some styling for better display

const RecentRentals = () => {
  const [properties, setProperties] = useState([]);

  // Demo houses with sample images
  const demoHouses = [
    {
      _id: 1,
      title: 'Luxury Villa in Nairobi',
      description: 'A stunning villa featuring 4 bedrooms, a private swimming pool, and a spacious garden.',
      price: 150000,
      image: 'https://via.placeholder.com/400x300?text=Luxury+Villa+in+Nairobi', // Replace with actual image
    },
    {
      _id: 2,
      title: 'Cozy Apartment in Mombasa',
      description: 'A cozy 2-bedroom apartment just a stone’s throw from the beach. Perfect for a serene coastal lifestyle.',
      price: 75000,
      image: 'https://via.placeholder.com/400x300?text=Cozy+Apartment+in+Mombasa',
    },
    {
      _id: 3,
      title: 'Spacious Bungalow in Kisumu',
      description: 'Modern 3-bedroom bungalow with an expansive garden. Ideal for family living in Kisumu.',
      price: 120000,
      image: 'https://via.placeholder.com/400x300?text=Spacious+Bungalow+in+Kisumu',
    },
    {
      _id: 4,
      title: 'Penthouse in Nakuru',
      description: 'A luxurious penthouse with breathtaking views of Lake Nakuru. Experience the height of sophistication.',
      price: 180000,
      image: 'https://via.placeholder.com/400x300?text=Penthouse+in+Nakuru',
    },
  ];

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await axios.get('http://localhost:5000/properties/recent');
        setProperties(response.data);
      } catch (error) {
        console.error('Error fetching recent rentals:', error);
        // If there's an error, fallback to demo houses
      }
    };

    fetchProperties();
  }, []);

  return (
    <div className="recent-rentals-container">
      <h2>Recent Rentals</h2>
      <div className="property-list">
        {(properties.length ? properties : demoHouses).map((property) => (
          <div key={property._id} className="property-card">
            <img src={property.image || 'https://via.placeholder.com/400x300?text=No+Image+Available'} 
                 alt={property.title} 
                 className="property-image" />
            <h3>{property.title}</h3>
            <p>{property.description}</p>
            <p><strong>Price:</strong> Ksh {property.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecentRentals;
