import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Map from './Map';
import Slider from './Slider';
import HeroSection from './HeroSection';
import ServiceSection from './ServiceSection';
import AboutUsSection from './AboutUsSection';
import BlogSection from './BlogSection';
import TestimonialsSection from './TestimonialsSection';
import ContactSection from './ContactSection';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './Home.css';

const countyData = {
  'Central': {
    counties: ['Kiambu', 'Murang\'a', 'Nyeri', 'Kirinyaga', 'Nyandarua'],
    subcounties: {
      'Kiambu': ['Thika', 'Kiambu Town', 'Ruiru'],
      'Murang\'a': ['Kangema', 'Kigumo', 'Mathioya'],
      'Nyeri': ['Kieni', 'Mathira', 'Tetu'],
      'Kirinyaga': ['Kirinyaga Central', 'Mwea East', 'Gichugu'],
      'Nyandarua': ['Kinangop', 'Ol Kalou', 'Ndaragwa'],
    },
  },
  'Coast': {
    counties: ['Mombasa', 'Kwale', 'Kilifi', 'Tana River', 'Lamu', 'Taita Taveta'],
    subcounties: {
      'Mombasa': ['Mvita', 'Kisauni', 'Likoni'],
      'Kwale': ['Kinango', 'Msambweni', 'Matuga'],
      'Kilifi': ['Malindi', 'Magarini', 'Rabai'],
      'Tana River': ['Galole', 'Garsen', 'Bura'],
      'Lamu': ['Lamu East', 'Lamu West'],
      'Taita Taveta': ['Voi', 'Taveta', 'Wundanyi'],
    },
  },
  'Eastern': {
    counties: ['Machakos', 'Kitui', 'Makueni', 'Embu', 'Meru', 'Tharaka Nithi', 'Isiolo'],
    subcounties: {
      'Machakos': ['Masinga', 'Yatta', 'Kangundo'],
      'Kitui': ['Kitui Central', 'Mwingi', 'Mutitu'],
      'Makueni': ['Mbooni', 'Kilungu', 'Kibwezi'],
      'Embu': ['Manyatta', 'Runyenjes', 'Mbeere'],
      'Meru': ['Imenti North', 'Igembe', 'Tigania'],
      'Tharaka Nithi': ['Tharaka', 'Chuka/Igambang\'ombe', 'Maara'],
      'Isiolo': ['Isiolo North', 'Isiolo South'],
    },
  },
  'Nairobi': {
    counties: ['Nairobi'],
    subcounties: {
      'Nairobi': ['Westlands', 'Lang\'ata', 'Embakasi', 'Starehe', 'Kasarani', 'Dagoretti'],
    },
  },
  'North Eastern': {
    counties: ['Garissa', 'Wajir', 'Mandera'],
    subcounties: {
      'Garissa': ['Garissa Township', 'Ijara', 'Dadaab'],
      'Wajir': ['Wajir East', 'Wajir South', 'Wajir West'],
      'Mandera': ['Mandera East', 'Mandera North', 'Mandera South'],
    },
  },
  'Nyanza': {
    counties: ['Kisumu', 'Siaya', 'Migori', 'Homa Bay', 'Kisii', 'Nyamira'],
    subcounties: {
      'Kisumu': ['Kisumu East', 'Kisumu West', 'Nyando'],
      'Siaya': ['Bondo', 'Alego Usonga', 'Rarieda'],
      'Migori': ['Rongo', 'Awendo', 'Uriri'],
      'Homa Bay': ['Rachuonyo', 'Ndhiwa', 'Mbita'],
      'Kisii': ['Kitutu Chache', 'Nyaribari Chache', 'Bonchari'],
      'Nyamira': ['Borabu', 'Manga', 'Nyamira North'],
    },
  },
  'Rift Valley': {
    counties: ['Nakuru', 'Uasin Gishu', 'Elgeyo Marakwet', 'Baringo', 'Kericho', 'Nandi', 'Narok', 'Kajiado', 'Samburu', 'Turkana', 'West Pokot', 'Trans Nzoia', 'Laikipia', 'Bomet'],
    subcounties: {
      'Nakuru': ['Naivasha', 'Nakuru Town West', 'Molo'],
      'Uasin Gishu': ['Eldoret East', 'Eldoret West', 'Soy'],
      'Elgeyo Marakwet': ['Keiyo North', 'Keiyo South', 'Marakwet West'],
      'Baringo': ['Baringo North', 'Baringo Central', 'Eldama Ravine'],
      'Kericho': ['Belgut', 'Bureti', 'Ainamoi'],
      'Nandi': ['Nandi Hills', 'Aldai', 'Tinderet'],
      'Narok': ['Narok North', 'Narok South', 'Trans Mara'],
      'Kajiado': ['Kajiado North', 'Kajiado Central', 'Loitokitok'],
      'Samburu': ['Samburu North', 'Samburu East', 'Samburu Central'],
      'Turkana': ['Turkana North', 'Turkana South', 'Turkana East'],
      'West Pokot': ['Kapenguria', 'Sigor', 'Kacheliba'],
      'Trans Nzoia': ['Kiminini', 'Kwanza', 'Saboti'],
      'Laikipia': ['Laikipia East', 'Laikipia North', 'Laikipia West'],
      'Bomet': ['Bomet Central', 'Bomet East', 'Chepalungu'],
    },
  },
  'Western': {
    counties: ['Kakamega', 'Bungoma', 'Vihiga', 'Busia'],
    subcounties: {
      'Kakamega': ['Lugari', 'Likuyani', 'Shinyalu'],
      'Bungoma': ['Kanduyi', 'Kimilili', 'Mt. Elgon'],
      'Vihiga': ['Vihiga', 'Sabatia', 'Emuhaya'],
      'Busia': ['Teso North', 'Teso South', 'Matayos'],
    },
  },
};

const Home = () => {
  const [properties, setProperties] = useState([]);
  const [search, setSearch] = useState('');
  const [featuredProperties, setFeaturedProperties] = useState([]);
  const [regions, setRegions] = useState(Object.keys(countyData));
  const [counties, setCounties] = useState([]);
  const [subcounties, setSubcounties] = useState([]);
  const [region, setRegion] = useState('');
  const [county, setCounty] = useState('');
  const [subcounty, setSubcounty] = useState('');

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        let url = 'http://localhost:5000/properties';

        if (region) {
          url += `?region=${region}`;
          if (county) {
            url += `&county=${county}`;
            if (subcounty) {
              url += `&subcounty=${subcounty}`;
            }
          }
        }

        const response = await axios.get(url);
        setProperties(response.data);
      } catch (error) {
        console.error('Error fetching properties:', error);
      }
    };

    const fetchFeaturedProperties = async () => {
      try {
        const response = await axios.get('http://localhost:5000/properties/featured');
        setFeaturedProperties(response.data);
      } catch (error) {
        console.error('Error fetching featured properties:', error);
      }
    };

    fetchProperties();
    fetchFeaturedProperties();
  }, [region, county, subcounty]);

  const handleSearchClick = () => {
    fetchProperties();
  };

  const handleRegionChange = (e) => {
    const selectedRegion = e.target.value;
    setRegion(selectedRegion);
    setCounty('');
    setSubcounty('');
    setCounties(countyData[selectedRegion]?.counties || []);
    setSubcounties([]);
  };

  const handleCountyChange = (e) => {
    const selectedCounty = e.target.value;
    setCounty(selectedCounty);
    setSubcounty('');
    setSubcounties(countyData[region]?.subcounties[selectedCounty] || []);
  };

  const handleSubcountyChange = (e) => {
    setSubcounty(e.target.value);
  };

  const filteredProperties = properties.filter((property) =>
    property.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container">
      <div className="search-container">
        <div className="filters">
          <label htmlFor="region">Region:</label>
          <select id="region" value={region} onChange={handleRegionChange}>
            <option value="">Select Region</option>
            {regions.map((reg) => (
              <option key={reg} value={reg}>{reg}</option>
            ))}
          </select>

          {counties.length > 0 && (
            <>
              <label htmlFor="county">County:</label>
              <select id="county" value={county} onChange={handleCountyChange}>
                <option value="">Select County</option>
                {counties.map((cty) => (
                  <option key={cty} value={cty}>{cty}</option>
                ))}
              </select>
            </>
          )}

          {subcounties.length > 0 && (
            <>
              <label htmlFor="subcounty">Subcounty:</label>
              <select id="subcounty" value={subcounty} onChange={handleSubcountyChange}>
                <option value="">Select Subcounty</option>
                {subcounties.map((sub) => (
                  <option key={sub} value={sub}>{sub}</option>
                ))}
              </select>
            </>
          )}

          <button className="search-button" onClick={handleSearchClick}>
            <FontAwesomeIcon icon={faSearch} /> Search
          </button>
        </div>
      </div>
      <HeroSection />
      <Slider />
      <ServiceSection />
      <AboutUsSection />
      <BlogSection />
      <TestimonialsSection />
      <ContactSection />
      <Map properties={filteredProperties} />
    </div>
  );
};

export default Home;
