import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css'; // Ensure this path is correct

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    description: '',
    password: '',
    profilePicture: null,
  });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      profilePicture: e.target.files[0],
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, description, password, profilePicture } = formData;
    const data = new FormData();
    data.append('name', name);
    data.append('email', email);
    data.append('description', description);
    data.append('password', password);
    data.append('profilePicture', profilePicture);

    try {
      const response = await axios.post('http://localhost:5000/register', data);
      if (response.status === 200) {
        navigate('/login');
      }
    } catch (error) {
      console.error('There was an error registering!', error);
      setError('Registration failed. Please try again.');
    }
  };

  return (
    <div className="register-container">
      <h1>Register</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name</label>
        <input type="text" id="name" name="name" placeholder="Name" onChange={handleChange} required />

        <label htmlFor="email">Email</label>
        <input type="email" id="email" name="email" placeholder="Email" onChange={handleChange} required />

        <label htmlFor="description">Description</label>
        <textarea id="description" name="description" placeholder="Description" onChange={handleChange} required />

        <label htmlFor="password">Password</label>
        <input type="password" id="password" name="password" placeholder="Password" onChange={handleChange} required />

        <label htmlFor="profilePicture">Profile Picture</label>
        <input type="file" id="profilePicture" name="profilePicture" onChange={handleFileChange} required />

        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
